@app.route('/guidance', methods=['POST'])
def guidance():
    user_name=request.form['name']
    grade_10 = int(request.form['grade10'])
    grade_12 = int(request.form['grade12'])
    guidance_msg = ''

    if grade_12 >= 80:
        guidance_msg = 'Congratulations! You have performed exceptionally well in your 12th grade. You have a wide range of options to choose from for higher education.'
    elif grade_12 >= 60:
        if grade_10 >= 70:
            guidance_msg = 'You have performed well in your 12th grade. Consider pursuing a bachelor\'s degree in a field of your interest.'
        else:
            guidance_msg = 'You have performed decently in your 12th grade. It is recommended to focus on improving your grades in order to have more options for higher education.'
    else:
        guidance_msg="less score u have scored"
    return render_template('guidance.html', guidance_msg=guidance_msg, user_name=user_name)
